const menuToggle=document.querySelector(".navigation__toggle"),navigation=document.querySelector(".navigation");menuToggle.addEventListener("click",(()=>{navigation.classList.toggle("open")}));const list=document.querySelectorAll(".navigation__link");function activeLink(){list.forEach((e=>{e.classList.remove("active"),this.classList.add("active")}))}list.forEach((e=>e.addEventListener("click",activeLink)));